import * as yandex from "@pulumi/yandex";

const test_backend_group = new yandex.AlbBackendGroup("test-backend-group", {
    httpBackends: [{
        healthcheck: {
            httpHealthcheck: {
                path: "/",
            },
            interval: "1s",
            timeout: "1s",
        },
        http2: true,
        loadBalancingConfig: {
            panicThreshold: 50,
        },
        name: "test-http-backend",
        port: 8080,
        targetGroupIds: [yandex_alb_target_group["test-target-group"].id],
        tls: {
            sni: "backend-domain.internal",
        },
        weight: 1,
    }],
    sessionAffinity: {
        connection: {
            sourceIp: "127.0.0.1",
        },
    },
});
