const repo_1 = new yandex.ContainerRepository("repo-1", {});
const puller = new yandex.ContainerRepositoryIamBinding("puller", {
    repositoryId: repo_1.id,
    role: "container-registry.images.puller",
    members: ["system:allUsers"],
});
