import com.pulumi.yandex.ContainerRepositoryLifecyclePolicy;
import com.pulumi.yandex.ContainerRepositoryLifecyclePolicyArgs;
import com.pulumi.yandex.inputs.ContainerRepositoryLifecyclePolicyRuleArgs;
import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        var myRegistry = new ContainerRegistry("myRegistry");

        var myRepository = new ContainerRepository("myRepository");

        var myLifecyclePolicy = new ContainerRepositoryLifecyclePolicy("myLifecyclePolicy", ContainerRepositoryLifecyclePolicyArgs.builder()        
            .status("active")
            .repositoryId(myRepository.id())
            .rules(ContainerRepositoryLifecyclePolicyRuleArgs.builder()
                .description("my description")
                .untagged(true)
                .tagRegexp(".*")
                .retainedTop(1)
                .build())
            .build());

    }
}
