import com.pulumi.yandex.inputs.GetResourcemanagerFolderArgs;
import com.pulumi.yandex.ResourcemanagerFolderIamBinding;
import com.pulumi.yandex.ResourcemanagerFolderIamBindingArgs;
import com.pulumi.yandex.IamServiceAccountStaticAccessKey;
import com.pulumi.yandex.IamServiceAccountStaticAccessKeyArgs;
import com.pulumi.yandex.StorageBucket;
import com.pulumi.yandex.StorageBucketArgs;
import com.pulumi.yandex.DataprocCluster;
import com.pulumi.yandex.DataprocClusterArgs;
import com.pulumi.yandex.inputs.DataprocClusterClusterConfigArgs;
import com.pulumi.yandex.inputs.DataprocClusterClusterConfigHadoopArgs;
import com.pulumi.resources.CustomResourceOptions;
import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        var fooVpcNetwork = new VpcNetwork("fooVpcNetwork");

        var fooVpcSubnet = new VpcSubnet("fooVpcSubnet", VpcSubnetArgs.builder()        
            .zone("ru-central1-b")
            .networkId(fooVpcNetwork.id())
            .v4CidrBlocks("10.1.0.0/24")
            .build());

        var dataprocIamServiceAccount = new IamServiceAccount("dataprocIamServiceAccount", IamServiceAccountArgs.builder()        
            .description("service account to manage Dataproc Cluster")
            .build());

        final var fooResourcemanagerFolder = YandexFunctions.getResourcemanagerFolder(GetResourcemanagerFolderArgs.builder()
            .folderId("some_folder_id")
            .build());

        var dataprocResourcemanagerFolderIamBinding = new ResourcemanagerFolderIamBinding("dataprocResourcemanagerFolderIamBinding", ResourcemanagerFolderIamBindingArgs.builder()        
            .folderId(fooResourcemanagerFolder.applyValue(getResourcemanagerFolderResult -> getResourcemanagerFolderResult.id()))
            .role("mdb.dataproc.agent")
            .members(dataprocIamServiceAccount.id().applyValue(id -> String.format("serviceAccount:%s", id)))
            .build());

        // required in order to create bucket
        var bucket_creator = new ResourcemanagerFolderIamBinding("bucket-creator", ResourcemanagerFolderIamBindingArgs.builder()        
            .folderId(fooResourcemanagerFolder.applyValue(getResourcemanagerFolderResult -> getResourcemanagerFolderResult.id()))
            .role("editor")
            .members(dataprocIamServiceAccount.id().applyValue(id -> String.format("serviceAccount:%s", id)))
            .build());

        var fooIamServiceAccountStaticAccessKey = new IamServiceAccountStaticAccessKey("fooIamServiceAccountStaticAccessKey", IamServiceAccountStaticAccessKeyArgs.builder()        
            .serviceAccountId(dataprocIamServiceAccount.id())
            .build());

        var fooStorageBucket = new StorageBucket("fooStorageBucket", StorageBucketArgs.builder()        
            .bucket("foo")
            .accessKey(fooIamServiceAccountStaticAccessKey.accessKey())
            .secretKey(fooIamServiceAccountStaticAccessKey.secretKey())
            .build(), CustomResourceOptions.builder()
                .dependsOn(bucket_creator)
                .build());

        var fooDataprocCluster = new DataprocCluster("fooDataprocCluster", DataprocClusterArgs.builder()        
            .bucket(fooStorageBucket.bucket())
            .description("Dataproc Cluster created by Terraform")
            .labels(Map.of("created_by", "terraform"))
            .serviceAccountId(dataprocIamServiceAccount.id())
            .zoneId("ru-central1-b")
            .clusterConfig(DataprocClusterClusterConfigArgs.builder()
                .hadoop(DataprocClusterClusterConfigHadoopArgs.builder()
                    .services(                    
                        "HDFS",
                        "YARN",
                        "SPARK",
                        "TEZ",
                        "MAPREDUCE",
                        "HIVE")
                    .properties(Map.of("yarn:yarn.resourcemanager.am.max-attempts", 5))
                    .sshPublicKeys(Files.readString(Paths.get("~/.ssh/id_rsa.pub")))
                    .initializationActions(DataprocClusterClusterConfigHadoopInitializationActionArgs.builder()
                        .uri("s3a://yandex_storage_bucket.foo.bucket/scripts/script.sh")
                        .args(                        
                            "arg1",
                            "arg2")
                        .build())
                    .build())
                .subclusterSpecs(                
                    DataprocClusterClusterConfigSubclusterSpecArgs.builder()
                        .name("main")
                        .role("MASTERNODE")
                        .resources(DataprocClusterClusterConfigSubclusterSpecResourcesArgs.builder()
                            .resourcePresetId("s2.small")
                            .diskTypeId("network-hdd")
                            .diskSize(20)
                            .build())
                        .subnetId(fooVpcSubnet.id())
                        .hostsCount(1)
                        .build(),
                    DataprocClusterClusterConfigSubclusterSpecArgs.builder()
                        .name("data")
                        .role("DATANODE")
                        .resources(DataprocClusterClusterConfigSubclusterSpecResourcesArgs.builder()
                            .resourcePresetId("s2.small")
                            .diskTypeId("network-hdd")
                            .diskSize(20)
                            .build())
                        .subnetId(fooVpcSubnet.id())
                        .hostsCount(2)
                        .build(),
                    DataprocClusterClusterConfigSubclusterSpecArgs.builder()
                        .name("compute")
                        .role("COMPUTENODE")
                        .resources(DataprocClusterClusterConfigSubclusterSpecResourcesArgs.builder()
                            .resourcePresetId("s2.small")
                            .diskTypeId("network-hdd")
                            .diskSize(20)
                            .build())
                        .subnetId(fooVpcSubnet.id())
                        .hostsCount(2)
                        .build(),
                    DataprocClusterClusterConfigSubclusterSpecArgs.builder()
                        .name("compute_autoscaling")
                        .role("COMPUTENODE")
                        .resources(DataprocClusterClusterConfigSubclusterSpecResourcesArgs.builder()
                            .resourcePresetId("s2.small")
                            .diskTypeId("network-hdd")
                            .diskSize(20)
                            .build())
                        .subnetId(fooVpcSubnet.id())
                        .hostsCount(2)
                        .autoscalingConfig(DataprocClusterClusterConfigSubclusterSpecAutoscalingConfigArgs.builder()
                            .maxHostsCount(10)
                            .measurementDuration(60)
                            .warmupDuration(60)
                            .stabilizationDuration(120)
                            .preemptible(false)
                            .decommissionTimeout(60)
                            .build())
                        .build())
                .build())
            .build(), CustomResourceOptions.builder()
                .dependsOn(dataprocResourcemanagerFolderIamBinding)
                .build());

    }
}
```
