import com.pulumi.yandex.inputs.MdbMysqlClusterResourcesArgs;
import com.pulumi.yandex.inputs.MdbMysqlClusterMaintenanceWindowArgs;
import com.pulumi.yandex.inputs.MdbMysqlClusterHostArgs;
import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        var fooVpcNetwork = new VpcNetwork("fooVpcNetwork");

        var fooVpcSubnet = new VpcSubnet("fooVpcSubnet", VpcSubnetArgs.builder()        
            .zone("ru-central1-a")
            .networkId(fooVpcNetwork.id())
            .v4CidrBlocks("10.1.0.0/24")
            .build());

        var bar = new VpcSubnet("bar", VpcSubnetArgs.builder()        
            .zone("ru-central1-b")
            .networkId(fooVpcNetwork.id())
            .v4CidrBlocks("10.2.0.0/24")
            .build());

        var fooMdbMysqlCluster = new MdbMysqlCluster("fooMdbMysqlCluster", MdbMysqlClusterArgs.builder()        
            .environment("PRESTABLE")
            .networkId(fooVpcNetwork.id())
            .version("8.0")
            .resources(MdbMysqlClusterResourcesArgs.builder()
                .resourcePresetId("s2.micro")
                .diskTypeId("network-ssd")
                .diskSize(16)
                .build())
            .maintenanceWindow(MdbMysqlClusterMaintenanceWindowArgs.builder()
                .type("WEEKLY")
                .day("SAT")
                .hour(12)
                .build())
            .hosts(            
                MdbMysqlClusterHostArgs.builder()
                    .zone("ru-central1-a")
                    .subnetId(fooVpcSubnet.id())
                    .build(),
                MdbMysqlClusterHostArgs.builder()
                    .zone("ru-central1-b")
                    .subnetId(bar.id())
                    .build())
            .build());

    }
}
