```typescript
import * as pulumi from "@pulumi/pulumi";
import * as yandex from "@pulumi/yandex";

const fooVpcNetwork = new yandex.VpcNetwork("fooVpcNetwork", {});
const fooVpcSubnet = new yandex.VpcSubnet("fooVpcSubnet", {
    zone: "ru-central1-a",
    networkId: fooVpcNetwork.id,
    v4CidrBlocks: ["10.5.0.0/24"],
});
const test_sg_x = new yandex.VpcSecurityGroup("test-sg-x", {
    networkId: fooVpcNetwork.id,
    ingresses: [{
        protocol: "ANY",
        description: "Allow incoming traffic from members of the same security group",
        fromPort: 0,
        toPort: 65535,
        v4CidrBlocks: ["0.0.0.0/0"],
    }],
    egresses: [{
        protocol: "ANY",
        description: "Allow outgoing traffic to members of the same security group",
        fromPort: 0,
        toPort: 65535,
        v4CidrBlocks: ["0.0.0.0/0"],
    }],
});
const fooMdbSqlserverCluster = new yandex.MdbSqlserverCluster("fooMdbSqlserverCluster", {
    environment: "PRESTABLE",
    networkId: fooVpcNetwork.id,
    version: "2016sp2std",
    resources: {
        resourcePresetId: "s2.small",
        diskTypeId: "network-ssd",
        diskSize: 20,
    },
    labels: {
        test_key: "test_value",
    },
    backupWindowStart: {
        hours: 20,
        minutes: 30,
    },
    sqlserverConfig: {
        fill_factor_percent: "49",
        optimize_for_ad_hoc_workloads: "true",
    },
    databases: [
        {
            name: "db_name_a",
        },
        {
            name: "db_name",
        },
        {
            name: "db_name_b",
        },
    ],
    users: [
        {
            name: "bob",
            password: "mysecurepassword",
        },
        {
            name: "alice",
            password: "mysecurepassword",
            permissions: [{
                databaseName: "db_name",
                roles: ["DDLADMIN"],
            }],
        },
        {
            name: "chuck",
            password: "mysecurepassword",
            permissions: [
                {
                    databaseName: "db_name_a",
                    roles: ["OWNER"],
                },
                {
                    databaseName: "db_name",
                    roles: [
                        "OWNER",
                        "DDLADMIN",
                    ],
                },
                {
                    databaseName: "db_name_b",
                    roles: [
                        "OWNER",
                        "DDLADMIN",
                    ],
                },
            ],
        },
    ],
    hosts: [{
        zone: "ru-central1-a",
        subnetId: fooVpcSubnet.id,
    }],
    securityGroupIds: [test_sg_x.id],
    hostGroupIds: [
        "host_group_1",
        "host_group_2",
    ],
});
